import { Component, OnInit } from '@angular/core';
import { ProfileService, Profile } from '../../../../services/profile.service';
import { AuthService } from '../../../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-profile',
  template: `
    <div class="profile-container">
      <div class="profile-header">
        <h1>My Profile</h1>
        <button class="btn btn-outline" (click)="editProfile()">
          Edit Profile
        </button>
      </div>

      <div class="profile-content" *ngIf="profile">
        <div class="profile-card">
          <div class="profile-image-section">
            <img 
              [src]="profile.profileImage || getDefaultImage()" 
              [alt]="profile.name"
              class="profile-image"
            >
            <div class="verification-badge">
              ✅ KU Verified
            </div>
          </div>

          <div class="profile-info">
            <h2>{{ profile.name }}, {{ profile.age }}</h2>
            <p class="profile-details">{{ profile.year }} • {{ profile.major }}</p>
            <p class="profile-bio">{{ profile.bio }}</p>

            <div class="interests-section" *ngIf="profile.interests.length > 0">
              <h3>Interests</h3>
              <div class="interests">
                <span 
                  *ngFor="let interest of profile.interests" 
                  class="interest-tag"
                >
                  {{ interest }}
                </span>
              </div>
            </div>

            <div class="preferences-section">
              <h3>Preferences</h3>
              <div class="preference-item">
                <strong>Looking for:</strong> {{ profile.lookingFor }}
              </div>
              <div class="preference-item">
                <strong>Age range:</strong> {{ profile.ageRange.min }} - {{ profile.ageRange.max }}
              </div>
              <div class="preference-item">
                <strong>Max distance:</strong> {{ profile.maxDistance }} miles
              </div>
            </div>
          </div>
        </div>

        <div class="profile-stats">
          <div class="stat-card">
            <h3>Profile Views</h3>
            <div class="stat-number">{{ profileViews }}</div>
          </div>
          <div class="stat-card">
            <h3>Likes Received</h3>
            <div class="stat-number">{{ likesReceived }}</div>
          </div>
          <div class="stat-card">
            <h3>Matches</h3>
            <div class="stat-number">{{ matches }}</div>
          </div>
        </div>
      </div>

      <div class="loading" *ngIf="isLoading">
        <div class="loading-spinner"></div>
        <p>Loading profile...</p>
      </div>

      <div class="error-message" *ngIf="errorMessage">
        {{ errorMessage }}
      </div>
    </div>
  `,
  styles: [`
    .profile-container {
      max-width: 1000px;
      margin: 0 auto;
      padding: 2rem;
    }

    .profile-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 2rem;
    }

    .profile-header h1 {
      color: white;
      font-size: 2.5rem;
    }

    .profile-content {
      display: grid;
      gap: 2rem;
    }

    .profile-card {
      background: white;
      border-radius: 20px;
      padding: 2rem;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
      display: grid;
      grid-template-columns: 300px 1fr;
      gap: 2rem;
    }

    .profile-image-section {
      position: relative;
    }

    .profile-image {
      width: 100%;
      height: 300px;
      object-fit: cover;
      border-radius: 15px;
    }

    .verification-badge {
      position: absolute;
      top: 1rem;
      right: 1rem;
      background: linear-gradient(135deg, #28a745, #20c997);
      color: white;
      padding: 0.5rem 1rem;
      border-radius: 20px;
      font-size: 0.8rem;
      font-weight: 600;
    }

    .profile-info h2 {
      color: #333;
      font-size: 2rem;
      margin-bottom: 0.5rem;
    }

    .profile-details {
      color: #6c757d;
      font-size: 1.1rem;
      margin-bottom: 1rem;
    }

    .profile-bio {
      color: #555;
      line-height: 1.6;
      margin-bottom: 2rem;
    }

    .interests-section, .preferences-section {
      margin-bottom: 2rem;
    }

    .interests-section h3, .preferences-section h3 {
      color: #667eea;
      margin-bottom: 1rem;
    }

    .interests {
      display: flex;
      flex-wrap: wrap;
      gap: 0.5rem;
    }

    .interest-tag {
      background: #f8f9fa;
      color: #6c757d;
      padding: 0.5rem 1rem;
      border-radius: 20px;
      font-size: 0.9rem;
    }

    .preference-item {
      margin-bottom: 0.5rem;
      color: #555;
    }

    .profile-stats {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 1rem;
    }

    .stat-card {
      background: white;
      border-radius: 15px;
      padding: 1.5rem;
      text-align: center;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    }

    .stat-card h3 {
      color: #6c757d;
      font-size: 0.9rem;
      margin-bottom: 0.5rem;
      text-transform: uppercase;
      letter-spacing: 1px;
    }

    .stat-number {
      font-size: 2rem;
      font-weight: 700;
      color: #667eea;
    }

    .loading {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      min-height: 400px;
      color: white;
    }

    .loading-spinner {
      width: 50px;
      height: 50px;
      border: 4px solid rgba(255, 255, 255, 0.3);
      border-top: 4px solid white;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin-bottom: 1rem;
    }

    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }

    .error-message {
      background: #f8d7da;
      color: #721c24;
      padding: 1rem;
      border-radius: 8px;
      text-align: center;
    }

    @media (max-width: 768px) {
      .profile-card {
        grid-template-columns: 1fr;
      }
      
      .profile-stats {
        grid-template-columns: 1fr;
      }
    }
  `]
})
export class ProfileComponent implements OnInit {
  profile: Profile | null = null;
  isLoading = false;
  errorMessage = '';
  profileViews = 0;
  likesReceived = 0;
  matches = 0;

  constructor(
    private profileService: ProfileService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.loadProfile();
  }

  loadProfile() {
    this.isLoading = true;
    this.errorMessage = '';

    this.profileService.getProfile().subscribe({
      next: (profile: any) => {
        this.profile = profile;
        this.isLoading = false;
        // Mock stats - in real app, these would come from API
        this.profileViews = Math.floor(Math.random() * 100) + 50;
        this.likesReceived = Math.floor(Math.random() * 20) + 5;
        this.matches = Math.floor(Math.random() * 10) + 2;
      },
      error: (error: any) => {
        this.errorMessage = 'Failed to load profile. Please try again.';
        this.isLoading = false;
      }
    });
  }

  editProfile() {
    this.router.navigate(['/profile/edit']);
  }

  getDefaultImage(): string {
    return 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400';
  }
}
