import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ChatService, Chat } from '../../../../services/chat.service';

@Component({
  selector: 'app-chat-list',
  template: `
    <div class="chat-list-container">
      <div class="chat-header">
        <h1>Messages</h1>
        <div class="chat-stats">
          <span class="stat">{{ chats.length }} conversations</span>
        </div>
      </div>

      <div class="chat-list" *ngIf="chats.length > 0">
        <div 
          *ngFor="let chat of chats" 
          class="chat-item"
          (click)="openChat(chat.id)"
        >
          <div class="chat-avatar">
            <img 
              [src]="getParticipantImage(chat)" 
              [alt]="getParticipantName(chat)"
            >
            <div class="online-indicator" *ngIf="isOnline(chat)"></div>
          </div>

          <div class="chat-content">
            <div class="chat-header-info">
              <h3 class="participant-name">{{ getParticipantName(chat) }}</h3>
              <span class="last-message-time" *ngIf="chat.lastMessage">
                {{ formatTime(chat.lastMessage.timestamp) }}
              </span>
            </div>
            
            <p class="last-message" *ngIf="chat.lastMessage">
              {{ chat.lastMessage.content }}
            </p>
            
            <div class="chat-meta">
              <span class="unread-count" *ngIf="chat.unreadCount > 0">
                {{ chat.unreadCount }}
              </span>
            </div>
          </div>
        </div>
      </div>

      <div class="no-chats" *ngIf="chats.length === 0 && !isLoading">
        <div class="no-chats-content">
          <h2>No conversations yet</h2>
          <p>Start swiping to find your matches!</p>
          <button class="btn btn-primary" (click)="goToMatches()">
            Find Matches
          </button>
        </div>
      </div>

      <div class="loading" *ngIf="isLoading">
        <div class="loading-spinner"></div>
        <p>Loading conversations...</p>
      </div>

      <div class="error-message" *ngIf="errorMessage">
        {{ errorMessage }}
      </div>
    </div>
  `,
  styles: [`
    .chat-list-container {
      max-width: 800px;
      margin: 0 auto;
      padding: 2rem;
    }

    .chat-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 2rem;
    }

    .chat-header h1 {
      color: white;
      font-size: 2.5rem;
    }

    .chat-stats .stat {
      background: rgba(255, 255, 255, 0.1);
      padding: 0.5rem 1rem;
      border-radius: 20px;
      color: white;
      font-size: 0.9rem;
    }

    .chat-list {
      background: white;
      border-radius: 20px;
      overflow: hidden;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    }

    .chat-item {
      display: flex;
      align-items: center;
      padding: 1.5rem;
      border-bottom: 1px solid #e9ecef;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .chat-item:hover {
      background: #f8f9fa;
    }

    .chat-item:last-child {
      border-bottom: none;
    }

    .chat-avatar {
      position: relative;
      margin-right: 1rem;
    }

    .chat-avatar img {
      width: 60px;
      height: 60px;
      border-radius: 50%;
      object-fit: cover;
    }

    .online-indicator {
      position: absolute;
      bottom: 2px;
      right: 2px;
      width: 16px;
      height: 16px;
      background: #28a745;
      border: 3px solid white;
      border-radius: 50%;
    }

    .chat-content {
      flex: 1;
      min-width: 0;
    }

    .chat-header-info {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 0.5rem;
    }

    .participant-name {
      font-size: 1.1rem;
      font-weight: 600;
      color: #333;
      margin: 0;
    }

    .last-message-time {
      font-size: 0.8rem;
      color: #6c757d;
    }

    .last-message {
      color: #6c757d;
      font-size: 0.9rem;
      margin: 0;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .chat-meta {
      display: flex;
      justify-content: flex-end;
      margin-top: 0.5rem;
    }

    .unread-count {
      background: #ff6b6b;
      color: white;
      padding: 0.25rem 0.75rem;
      border-radius: 15px;
      font-size: 0.8rem;
      font-weight: 600;
    }

    .no-chats {
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 400px;
    }

    .no-chats-content {
      text-align: center;
      background: white;
      padding: 3rem;
      border-radius: 20px;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    }

    .no-chats-content h2 {
      color: #667eea;
      margin-bottom: 1rem;
    }

    .loading {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      min-height: 400px;
      color: white;
    }

    .loading-spinner {
      width: 50px;
      height: 50px;
      border: 4px solid rgba(255, 255, 255, 0.3);
      border-top: 4px solid white;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin-bottom: 1rem;
    }

    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }

    .error-message {
      background: #f8d7da;
      color: #721c24;
      padding: 1rem;
      border-radius: 8px;
      text-align: center;
    }
  `]
})
export class ChatListComponent implements OnInit {
  chats: Chat[] = [];
  isLoading = false;
  errorMessage = '';

  constructor(
    private chatService: ChatService,
    private router: Router
  ) {}

  ngOnInit() {
    this.loadChats();
  }

  loadChats() {
    this.isLoading = true;
    this.errorMessage = '';

    this.chatService.getChats().subscribe({
      next: (chats: any) => {
        this.chats = chats;
        this.isLoading = false;
      },
      error: (error: any) => {
        this.errorMessage = 'Failed to load conversations. Please try again.';
        this.isLoading = false;
      }
    });
  }

  openChat(chatId: number) {
    this.router.navigate(['/chat', chatId]);
  }

  getParticipantName(chat: Chat): string {
    return chat.participants[0]?.name || 'Unknown';
  }

  getParticipantImage(chat: Chat): string {
    return chat.participants[0]?.profileImage || 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100';
  }

  isOnline(chat: Chat): boolean {
    // Mock online status - in real app, this would come from WebSocket
    return Math.random() > 0.5;
  }

  formatTime(timestamp: Date): string {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);

    if (minutes < 60) {
      return `${minutes}m ago`;
    } else if (hours < 24) {
      return `${hours}h ago`;
    } else {
      return `${days}d ago`;
    }
  }

  goToMatches() {
    this.router.navigate(['/matches']);
  }
}
