import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import { tap } from 'rxjs/operators';

export interface User {
  id: number;
  email: string;
  name: string;
  age: number;
  major: string;
  year: string;
  bio: string;
  interests: string[];
  profileImage?: string;
  isVerified: boolean;
}

export interface LoginRequest {
  email: string;
  password: string;
}

export interface RegisterRequest {
  email: string;
  password: string;
  name: string;
  age: number;
  major: string;
  year: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private currentUserSubject = new BehaviorSubject<User | null>(null);
  public currentUser$ = this.currentUserSubject.asObservable();
  
  private readonly API_URL = 'http://localhost:8080';

  constructor(private http: HttpClient) {
    // Check for stored token on service initialization
    const token = localStorage.getItem('jaymatch_token');
    if (token) {
      this.validateToken();
    }
  }

  login(credentials: LoginRequest): Observable<any> {
    return this.http.post(`${this.API_URL}/auth/login`, credentials).pipe(
      tap((response: any) => {
        if (response.token) {
          localStorage.setItem('jaymatch_token', response.token);
          this.currentUserSubject.next(response.user);
        }
      })
    );
  }

  register(userData: RegisterRequest): Observable<any> {
    return this.http.post(`${this.API_URL}/auth/register`, userData).pipe(
      tap((response: any) => {
        if (response.token) {
          localStorage.setItem('jaymatch_token', response.token);
          this.currentUserSubject.next(response.user);
        }
      })
    );
  }

  logout(): void {
    localStorage.removeItem('jaymatch_token');
    this.currentUserSubject.next(null);
  }

  isAuthenticated(): boolean {
    const token = localStorage.getItem('jaymatch_token');
    return !!token && token !== '';
  }

  getCurrentUser(): User | null {
    return this.currentUserSubject.value;
  }

  private validateToken(): void {
    const token = localStorage.getItem('jaymatch_token');
    if (token) {
      this.http.get(`${this.API_URL}/auth/validate`).subscribe({
        next: (user: any) => this.currentUserSubject.next(user),
        error: () => this.logout()
      });
    }
  }

  // KU Email verification
  verifyKUEmail(email: string): boolean {
    return email.endsWith('@ku.edu');
  }

  // Send MFA code
  sendMFACode(email: string): Observable<any> {
    return this.http.post(`${this.API_URL}/auth/send-mfa`, { email });
  }

  // Verify MFA code
  verifyMFACode(email: string, code: string): Observable<any> {
    return this.http.post(`${this.API_URL}/auth/verify-mfa`, { email, code });
  }
}
