import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ChatService, Message, Chat } from '../../../../services/chat.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-chat-window',
  template: `
    <div class="chat-window-container">
      <div class="chat-header">
        <button class="back-btn" (click)="goBack()">
          ‚Üê Back
        </button>
        <div class="chat-info">
          <img 
            [src]="participantImage" 
            [alt]="participantName"
            class="participant-avatar"
          >
          <div class="participant-details">
            <h3>{{ participantName }}</h3>
            <span class="online-status" *ngIf="isOnline">Online</span>
            <span class="online-status offline" *ngIf="!isOnline">Last seen {{ lastSeen }}</span>
          </div>
        </div>
      </div>

      <div class="chat-messages" #messagesContainer>
        <div 
          *ngFor="let message of messages" 
          class="message"
          [class.sent]="isSentByCurrentUser(message)"
          [class.received]="!isSentByCurrentUser(message)"
        >
          <div class="message-content">
            <p>{{ message.content }}</p>
            <span class="message-time">{{ formatMessageTime(message.timestamp) }}</span>
          </div>
        </div>
      </div>

      <form [formGroup]="messageForm" (ngSubmit)="sendMessage()" class="chat-input">
        <input 
          type="text" 
          formControlName="content"
          placeholder="Type a message..."
          class="message-input"
        >
        <button 
          type="submit" 
          class="send-btn"
          [disabled]="messageForm.invalid || isLoading"
        >
          <span *ngIf="!isLoading">Send</span>
          <span *ngIf="isLoading">Sending...</span>
        </button>
      </form>
    </div>
  `,
  styles: [`
    .chat-window-container {
      max-width: 800px;
      margin: 0 auto;
      height: calc(100vh - 120px);
      display: flex;
      flex-direction: column;
      background: white;
      border-radius: 20px;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
      overflow: hidden;
    }

    .chat-header {
      display: flex;
      align-items: center;
      padding: 1rem 1.5rem;
      background: linear-gradient(135deg, #667eea, #764ba2);
      color: white;
    }

    .back-btn {
      background: rgba(255, 255, 255, 0.2);
      color: white;
      border: none;
      padding: 0.5rem 1rem;
      border-radius: 8px;
      cursor: pointer;
      margin-right: 1rem;
      transition: background-color 0.3s ease;
    }

    .back-btn:hover {
      background: rgba(255, 255, 255, 0.3);
    }

    .chat-info {
      display: flex;
      align-items: center;
      flex: 1;
    }

    .participant-avatar {
      width: 50px;
      height: 50px;
      border-radius: 50%;
      object-fit: cover;
      margin-right: 1rem;
    }

    .participant-details h3 {
      margin: 0;
      font-size: 1.2rem;
    }

    .online-status {
      font-size: 0.8rem;
      opacity: 0.8;
    }

    .online-status.offline {
      opacity: 0.6;
    }

    .chat-messages {
      flex: 1;
      padding: 1rem;
      overflow-y: auto;
      background: #f8f9fa;
    }

    .message {
      margin-bottom: 1rem;
      display: flex;
    }

    .message.sent {
      justify-content: flex-end;
    }

    .message.received {
      justify-content: flex-start;
    }

    .message-content {
      max-width: 70%;
      padding: 0.75rem 1rem;
      border-radius: 18px;
      position: relative;
    }

    .message.sent .message-content {
      background: linear-gradient(135deg, #667eea, #764ba2);
      color: white;
    }

    .message.received .message-content {
      background: white;
      color: #333;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    }

    .message-content p {
      margin: 0;
      line-height: 1.4;
    }

    .message-time {
      font-size: 0.7rem;
      opacity: 0.7;
      display: block;
      margin-top: 0.25rem;
    }

    .chat-input {
      display: flex;
      padding: 1rem 1.5rem;
      background: white;
      border-top: 1px solid #e9ecef;
    }

    .message-input {
      flex: 1;
      padding: 0.75rem 1rem;
      border: 2px solid #e9ecef;
      border-radius: 25px;
      font-size: 1rem;
      outline: none;
      transition: border-color 0.3s ease;
    }

    .message-input:focus {
      border-color: #667eea;
    }

    .send-btn {
      margin-left: 1rem;
      padding: 0.75rem 1.5rem;
      background: linear-gradient(135deg, #667eea, #764ba2);
      color: white;
      border: none;
      border-radius: 25px;
      cursor: pointer;
      font-weight: 600;
      transition: all 0.3s ease;
    }

    .send-btn:hover:not(:disabled) {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(102, 126, 234, 0.3);
    }

    .send-btn:disabled {
      opacity: 0.6;
      cursor: not-allowed;
    }

    @media (max-width: 768px) {
      .chat-window-container {
        height: calc(100vh - 80px);
        border-radius: 0;
      }
      
      .message-content {
        max-width: 85%;
      }
    }
  `]
})
export class ChatWindowComponent implements OnInit, OnDestroy {
  chatId!: number;
  messages: Message[] = [];
  participantName = '';
  participantImage = '';
  isOnline = false;
  lastSeen = '';
  isLoading = false;
  
  messageForm: FormGroup;
  private messagesSubscription?: Subscription;

