    // Mock chat data - Diana Heart as default chatter
    if (this.chatId === 1) {
      this.participantName = 'Diana Heart';
      this.participantImage = 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=100';
      this.isOnline = true;
      this.lastSeen = 'Online now';
    } else {
      this.participantName = 'Alex Johnson';
      this.participantImage = 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100';
      this.isOnline = Math.random() > 0.5;
      this.lastSeen = '2 hours ago';
    }
  }

  loadMessages() {
    // Mock messages for Diana Heart
    if (this.chatId === 1) {
      this.messages = [
        {
          id: 1,
          senderId: 2,
          receiverId: 1,
          content: 'Hey! Welcome to JayMatch! I\'m Diana Heart, your personal matchmaking assistant üíú',
          timestamp: new Date(Date.now() - 3600000),
          isRead: true
        },
        {
          id: 2,
          senderId: 2,
          receiverId: 1,
          content: 'I\'m here to help you find your perfect KU match! How\'s your day going?',
          timestamp: new Date(Date.now() - 1800000),
          isRead: true
        },
        {
          id: 3,
          senderId: 2,
          receiverId: 1,
          content: 'Ready to start swiping? I\'ve got some amazing profiles lined up for you! üöÄ',
          timestamp: new Date(),
          isRead: false
        }
      ];
    } else {
      this.messages = [
        {
          id: 1,
          senderId: 2,
          receiverId: 1,
          content: 'Hey! How\'s your day going?',
          timestamp: new Date(Date.now() - 3600000),
          isRead: true
        },
        {
          id: 2,
          senderId: 1,
          receiverId: 2,
          content: 'Great! Just finished my CS homework. How about you?',
          timestamp: new Date(Date.now() - 1800000),
          isRead: true
        }
      ];
    }
    this.scrollToBottom();
  }

  connectToWebSocket() {
    this.chatService.connectToChat(this.chatId);
    
    this.messagesSubscription = this.chatService.messages$.subscribe((messages: any) => {
      this.messages = messages;
      this.scrollToBottom();
    });
  }

  sendMessage() {
    if (this.messageForm.valid) {
      const content = this.messageForm.value.content;
      this.isLoading = true;

      this.chatService.sendMessage(this.chatId, content).subscribe({
        next: (message: any) => {
          this.messages.push(message);
          this.messageForm.reset();
          this.isLoading = false;
          this.scrollToBottom();
        },
        error: (error: any) => {
          console.error('Error sending message:', error);
          this.isLoading = false;
        }
      });
    }
  }

  isSentByCurrentUser(message: Message): boolean {
    // Mock - in real app, compare with current user ID
    return Math.random() > 0.5;
  }

  formatMessageTime(timestamp: Date): string {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }

  scrollToBottom() {
    // Scroll to bottom of messages container
    setTimeout(() => {
      const container = document.querySelector('.chat-messages');
      if (container) {
        container.scrollTop = container.scrollHeight;
      }
    }, 100);
  }

  goBack() {
    this.router.navigate(['/chat']);
  }
}
