import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../../../services/auth.service';

@Component({
  selector: 'app-login',
  template: `
    <div class="auth-container">
      <div class="auth-card">
        <div class="auth-header">
          <h1>❤️ JayMatch</h1>
          <p class="subtitle">Welcome back, Jayhawk!</p>
        </div>

        <form [formGroup]="loginForm" (ngSubmit)="onSubmit()" class="auth-form">
          <div class="form-group">
            <label for="email">KU Email</label>
            <input 
              type="email" 
              id="email"
              formControlName="email"
              placeholder="yourname@ku.edu"
              [class.error]="loginForm.get('email')?.invalid && loginForm.get('email')?.touched"
            >
            <div class="error-message" *ngIf="loginForm.get('email')?.invalid && loginForm.get('email')?.touched">
              <span *ngIf="loginForm.get('email')?.errors?.['required']">Email is required</span>
              <span *ngIf="loginForm.get('email')?.errors?.['email']">Please enter a valid email</span>
              <span *ngIf="loginForm.get('email')?.errors?.['kuEmail']">Must be a KU email (&#64;ku.edu)</span>
            </div>
          </div>

          <div class="form-group">
            <label for="password">Password</label>
            <input 
              type="password" 
              id="password"
              formControlName="password"
              placeholder="Enter your password"
              [class.error]="loginForm.get('password')?.invalid && loginForm.get('password')?.touched"
            >
            <div class="error-message" *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched">
              Password is required
            </div>
          </div>

          <button 
            type="submit" 
            class="btn btn-primary full-width"
            [disabled]="loginForm.invalid || isLoading"
          >
            <span *ngIf="!isLoading">Sign In</span>
            <span *ngIf="isLoading">Signing In...</span>
          </button>
        </form>

        <!-- Demo Bypass Button -->
        <div class="demo-bypass">
          <button 
            class="btn btn-demo"
            (click)="demoBypass()"
            [disabled]="isLoading"
          >
            🚀 Demo Bypass (Skip Login)
          </button>
          <p class="demo-note">For demo purposes only</p>
        </div>

        <div class="auth-footer">
          <p>Don't have an account? <a routerLink="/register">Sign up</a></p>
        </div>

        <div class="error-alert" *ngIf="errorMessage">
          {{ errorMessage }}
        </div>
      </div>
    </div>
  `,
  styles: [`
    .auth-container {
      min-height: 100vh;
      display: flex;
      align-items: center;
      justify-content: center;
      padding: 2rem;
    }

    .auth-card {
      background: white;
      border-radius: 20px;
      padding: 3rem;
      box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
      width: 100%;
      max-width: 400px;
    }

    .auth-header {
      text-align: center;
      margin-bottom: 2rem;
    }

    .auth-header h1 {
      color: #667eea;
      font-size: 2.5rem;
      margin-bottom: 0.5rem;
    }

    .subtitle {
      color: #6c757d;
      font-size: 1.1rem;
    }

    .auth-form {
      margin-bottom: 2rem;
    }

    .full-width {
      width: 100%;
      margin-top: 1rem;
    }

    .auth-footer {
      text-align: center;
      color: #6c757d;
    }

    .auth-footer a {
      color: #667eea;
      text-decoration: none;
      font-weight: 500;
    }

    .auth-footer a:hover {
      text-decoration: underline;
    }

    .error-message {
      color: #dc3545;
      font-size: 0.875rem;
      margin-top: 0.25rem;
    }

    .error-alert {
      background: #f8d7da;
      color: #721c24;
      padding: 1rem;
      border-radius: 8px;
      margin-top: 1rem;
      border: 1px solid #f5c6cb;
    }

    input.error {
      border-color: #dc3545;
    }

    @media (max-width: 480px) {
      .auth-card {
        padding: 2rem;
        margin: 1rem;
      }
    }
  `]
})
export class LoginComponent {
  loginForm: FormGroup;
  isLoading = false;
  errorMessage = '';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email, this.kuEmailValidator]],
      password: ['', Validators.required]
    });
  }

  kuEmailValidator(control: any) {
    const email = control.value;
    if (email && !email.endsWith('@ku.edu')) {
      return { kuEmail: true };
    }
    return null;
  }

  onSubmit() {
    if (this.loginForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';

      this.authService.login(this.loginForm.value).subscribe({
        next: (response: any) => {
          this.isLoading = false;
          this.router.navigate(['/matches']);
        },
        error: (error: any) => {
          this.isLoading = false;
          this.errorMessage = error.error?.message || 'Login failed. Please try again.';
        }
      });
    }
  }

  demoBypass() {
    // Demo bypass - simulate successful login
    this.isLoading = true;
    
    // Simulate API call delay
    setTimeout(() => {
      // Set demo token and user data
      localStorage.setItem('jaymatch_token', 'demo-token-12345');
      localStorage.setItem('jaymatch_user', JSON.stringify({
        id: 1,
        email: 'demo@ku.edu',
        name: 'Demo User',
        age: 22,
        major: 'Computer Science',
        year: 'Senior',
        bio: 'Demo user for testing',
        interests: ['Programming', 'Coffee', 'Movies'],
        isVerified: true
      }));
      
      this.isLoading = false;
      this.router.navigate(['/matches']);
    }, 1000);
  }
}
