export class AppComponent {
  isAuthenticated = false;
  currentView = 'swipe';
  showRegister = false;
  
  loginForm = {
    email: '',
    password: ''
  };

  login() {
    // Simple validation
    if (this.loginForm.email && this.loginForm.password) {
      this.isAuthenticated = true;
      this.currentView = 'swipe';
    }
  }

  bypassLogin() {
    this.isAuthenticated = true;
    this.currentView = 'swipe';
  }

  logout() {
    this.isAuthenticated = false;
    this.loginForm = { email: '', password: '' };
  }

