import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

interface Profile {
  id: number;
  name: string;
  age: number;
  bio: string;
  images: string[];
  prompts: Array<{
    question: string;
    answer: string;
  }>;
  interests: string[];
  verified: boolean;
  activeToday: boolean;
}

@Component({
  selector: 'app-hinge-matches',
  template: `
    <div class="hinge-container">
      <!-- Header -->
      <div class="hinge-header">
        <div class="header-content">
          <h1>❤️ JayMatch</h1>
          <span class="ku-badge">KU Verified</span>
        </div>
        <div class="header-actions">
          <button class="btn btn-outline" (click)="goToProfile()">
            Edit Profile
          </button>
          <button class="btn btn-secondary" (click)="logout()">
            Logout
          </button>
        </div>
      </div>

      <!-- Profile Card -->
      <div class="profile-card" *ngIf="currentProfile">
        <!-- Profile Header -->
        <div class="profile-header">
          <div class="profile-info">
            <h2>{{ currentProfile.name }}</h2>
            <div class="profile-badges">
              <span class="verified-badge" *ngIf="currentProfile.verified">✓</span>
              <span class="active-badge" *ngIf="currentProfile.activeToday">Active today</span>
            </div>
          </div>
          <div class="profile-actions">
            <button class="action-btn heart-btn" (click)="likeProfile()">
              ❤️
            </button>
            <button class="action-btn menu-btn">
              ⋯
            </button>
          </div>
        </div>

        <!-- Main Profile Image -->
        <div class="main-image-container">
          <img 
            [src]="currentProfile.images[0]" 
            [alt]="currentProfile.name"
            class="main-image"
          >
          <div class="image-overlay">
            <button class="overlay-btn heart-overlay" (click)="likeProfile()">
              ❤️
            </button>
          </div>
        </div>

        <!-- Profile Prompts -->
        <div class="prompts-section">
          <div class="prompt" *ngFor="let prompt of currentProfile.prompts">
            <h3>{{ prompt.question }}</h3>
            <p>{{ prompt.answer }}</p>
          </div>
        </div>

        <!-- Interests -->
        <div class="interests-section">
          <div class="interest-tags">
            <span 
              *ngFor="let interest of currentProfile.interests" 
              class="interest-tag"
            >
              {{ interest }}
            </span>
          </div>
        </div>

        <!-- Action Buttons -->
        <div class="action-buttons">
          <button class="action-btn pass-btn" (click)="passProfile()">
            ✕
          </button>
          <button class="action-btn super-like-btn" (click)="superLikeProfile()">
            ⭐
          </button>
          <button class="action-btn like-btn" (click)="likeProfile()">
            ❤️
          </button>
        </div>

        <!-- Show More Button -->
        <div class="show-more-section">
          <button class="btn btn-primary show-more-btn" (click)="showMore()">
            Show More About {{ currentProfile.name }}
          </button>
        </div>
      </div>

      <!-- Chat with Diana Heart -->
      <div class="diana-chat-section">
        <div class="diana-card">
          <div class="diana-header">
            <img 
              src="https://images.unsplash.com/photo-1494790108755-2616b612b786?w=100" 
              alt="Diana Heart"
              class="diana-avatar"
            >
            <div class="diana-info">
              <h3>Diana Heart</h3>
              <p>Your default chat partner</p>
            </div>
            <div class="chat-notification">
              <span class="notification-badge">1</span>
            </div>
          </div>
          <button class="btn btn-primary diana-chat-btn" (click)="chatWithDiana()">
            💬 Chat with Diana
          </button>
        </div>
      </div>

      <!-- No More Profiles -->
      <div class="no-profiles" *ngIf="!currentProfile">
        <div class="no-profiles-content">
          <h2>No more profiles!</h2>
          <p>Check back later for new matches</p>
          <button class="btn btn-primary" (click)="refreshProfiles()">
            Refresh
          </button>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .hinge-container {
      min-height: 100vh;
      background: #000000;
      color: #ffffff;
      padding: 0;
    }

    .hinge-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 1rem 2rem;
      background: #1f1f1f;
      border-bottom: 1px solid #333333;
    }

    .header-content {
      display: flex;
      align-items: center;
      gap: 1rem;
    }

    .header-content h1 {
      color: #ffffff;
      font-size: 1.5rem;
      font-weight: 700;
    }

    .ku-badge {
      background: linear-gradient(135deg, #8b5cf6, #a855f7);
      color: white;
      padding: 0.25rem 0.75rem;
      border-radius: 15px;
      font-size: 0.75rem;
      font-weight: 600;
    }

    .header-actions {
      display: flex;
      gap: 1rem;
    }

    .profile-card {
      max-width: 400px;
      margin: 2rem auto;
      background: #1f1f1f;
      border-radius: 20px;
      overflow: hidden;
      border: 1px solid #333333;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    }

    .profile-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 1.5rem;
      background: #1f1f1f;
    }

    .profile-info h2 {
      color: #ffffff;
      font-size: 1.8rem;
      margin-bottom: 0.5rem;
    }

    .profile-badges {
      display: flex;
      gap: 0.5rem;
      align-items: center;
    }

    .verified-badge {
      background: linear-gradient(135deg, #8b5cf6, #a855f7);
      color: white;
      padding: 0.25rem 0.5rem;
      border-radius: 50%;
      font-size: 0.8rem;
    }

    .active-badge {
      color: #8b5cf6;
      font-size: 0.9rem;
    }

    .profile-actions {
      display: flex;
      gap: 0.5rem;
    }

    .action-btn {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      border: none;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 1.2rem;
      transition: all 0.3s ease;
    }

    .heart-btn {
      background: rgba(255, 255, 255, 0.1);
      color: #ff6b6b;
    }

    .menu-btn {
      background: rgba(255, 255, 255, 0.1);
      color: #ffffff;
    }

    .main-image-container {
      position: relative;
      height: 500px;
      overflow: hidden;
    }

    .main-image {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }

    .image-overlay {
      position: absolute;
      top: 0;
      right: 0;
      width: 100%;
      height: 100%;
      display: flex;
      align-items: center;
      justify-content: flex-end;
      padding: 1rem;
    }

    .overlay-btn {
      width: 60px;
      height: 60px;
      border-radius: 50%;
      background: rgba(255, 255, 255, 0.9);
      border: none;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 1.5rem;
      transition: all 0.3s ease;
    }

    .heart-overlay:hover {
      transform: scale(1.1);
      background: #ff6b6b;
      color: white;
    }

    .prompts-section {
      padding: 1.5rem;
    }

    .prompt {
      margin-bottom: 1.5rem;
      padding: 1rem;
      background: #2a2a2a;
      border-radius: 12px;
      border-left: 4px solid #8b5cf6;
    }

    .prompt h3 {
      color: #8b5cf6;
      font-size: 0.9rem;
      margin-bottom: 0.5rem;
      font-weight: 600;
    }

    .prompt p {
      color: #ffffff;
      font-size: 1rem;
      line-height: 1.5;
    }

    .interests-section {
      padding: 0 1.5rem 1.5rem;
    }

    .interest-tags {
      display: flex;
      flex-wrap: wrap;
      gap: 0.5rem;
    }

    .interest-tag {
      background: #2a2a2a;
      color: #ffffff;
      padding: 0.5rem 1rem;
      border-radius: 20px;
      font-size: 0.9rem;
      border: 1px solid #333333;
    }

    .action-buttons {
      display: flex;
      justify-content: center;
      gap: 2rem;
      padding: 1.5rem;
      background: #1f1f1f;
    }

    .pass-btn {
      background: #ff4757;
      color: white;
      width: 60px;
      height: 60px;
    }

    .super-like-btn {
      background: #3742fa;
      color: white;
      width: 50px;
      height: 50px;
    }

    .like-btn {
      background: #2ed573;
      color: white;
      width: 60px;
      height: 60px;
    }

    .action-btn:hover {
      transform: scale(1.1);
    }

    .show-more-section {
      padding: 1.5rem;
      background: #1f1f1f;
    }

    .show-more-btn {
      width: 100%;
      padding: 1rem;
      font-size: 1.1rem;
    }

    .diana-chat-section {
      padding: 2rem;
      max-width: 400px;
      margin: 0 auto;
    }

    .diana-card {
      background: #1f1f1f;
      border-radius: 16px;
      padding: 1.5rem;
      border: 1px solid #333333;
    }

    .diana-header {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 1rem;
    }

    .diana-avatar {
      width: 50px;
      height: 50px;
      border-radius: 50%;
      object-fit: cover;
    }

    .diana-info h3 {
      color: #ffffff;
      margin-bottom: 0.25rem;
    }

    .diana-info p {
      color: #8b5cf6;
      font-size: 0.9rem;
    }

    .chat-notification {
      margin-left: auto;
    }

    .notification-badge {
      background: #8b5cf6;
      color: white;
      padding: 0.25rem 0.5rem;
      border-radius: 50%;
      font-size: 0.8rem;
      font-weight: 600;
    }

    .diana-chat-btn {
      width: 100%;
      padding: 1rem;
    }

    .no-profiles {
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 400px;
    }

    .no-profiles-content {
      text-align: center;
      background: #1f1f1f;
      padding: 3rem;
      border-radius: 20px;
      border: 1px solid #333333;
    }

    .no-profiles-content h2 {
      color: #ffffff;
      margin-bottom: 1rem;
    }

    .no-profiles-content p {
      color: #8b5cf6;
      margin-bottom: 2rem;
    }

    @media (max-width: 480px) {
      .hinge-header {
        padding: 1rem;
        flex-direction: column;
        gap: 1rem;
      }

      .header-actions {
        width: 100%;
        justify-content: center;
      }

      .profile-card {
        margin: 1rem;
        max-width: none;
      }

      .main-image-container {
        height: 400px;
      }
    }
  `]
})
export class HingeMatchesComponent implements OnInit {
  currentProfile: Profile | null = null;
  profileIndex = 0;

  profiles: Profile[] = [
    {
      id: 1,
      name: 'Tessa',
      age: 22,
      bio: 'Love hiking and coffee!',
      images: ['https://images.unsplash.com/photo-1494790108755-2616b612b786?w=400'],
      prompts: [
        {
          question: "We'll instantly hit it off if",
          answer: "You love outdoor adventures and can keep up with my hiking pace! 🥾"
        },
        {
          question: "My simple pleasures",
          answer: "Morning coffee, sunset walks, and discovering new music"
        }
      ],
      interests: ['Hiking', 'Coffee', 'Music', 'Photography'],
      verified: true,
      activeToday: true
    },
    {
      id: 2,
      name: 'Alex',
      age: 23,
      bio: 'CS student who loves gaming',
      images: ['https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400'],
      prompts: [
        {
          question: "The way to my heart",
          answer: "Show me your favorite indie game or share your latest coding project!"
        },
        {
          question: "I'm weirdly attracted to",
          answer: "People who can explain complex topics in simple terms"
        }
      ],
      interests: ['Gaming', 'Programming', 'Anime', 'Pizza'],
      verified: true,
      activeToday: false
    },
    {
      id: 3,
      name: 'Sarah',
      age: 21,
      bio: 'Psychology major, dog lover',
      images: ['https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=400'],
      prompts: [
        {
          question: "My most irrational fear",
          answer: "That my dog will learn to open doors and escape"
        },
        {
          question: "I'll fall for you if",
          answer: "You can make me laugh until I cry"
        }
      ],
      interests: ['Dogs', 'Psychology', 'Comedy', 'Books'],
      verified: true,
      activeToday: true
    }
  ];

  constructor(private router: Router) {}

  ngOnInit() {
    this.loadCurrentProfile();
  }

  loadCurrentProfile() {
    if (this.profileIndex < this.profiles.length) {
      this.currentProfile = this.profiles[this.profileIndex];
    } else {
      this.currentProfile = null;
    }
  }

  likeProfile() {
    console.log('Liked:', this.currentProfile?.name);
    this.nextProfile();
  }

  passProfile() {
    console.log('Passed:', this.currentProfile?.name);
    this.nextProfile();
  }

  superLikeProfile() {
    console.log('Super liked:', this.currentProfile?.name);
    this.nextProfile();
  }

  nextProfile() {
    this.profileIndex++;
    this.loadCurrentProfile();
  }

  showMore() {
    // In a real app, this would show more details or additional photos
    console.log('Show more about:', this.currentProfile?.name);
  }

  chatWithDiana() {
    // Navigate to chat with Diana Heart
    this.router.navigate(['/chat']);
  }

  goToProfile() {
    this.router.navigate(['/profile']);
  }

  logout() {
    localStorage.removeItem('jaymatch_token');
    localStorage.removeItem('jaymatch_user');
    this.router.navigate(['/login']);
  }

  refreshProfiles() {
    this.profileIndex = 0;
    this.loadCurrentProfile();
  }
}
