import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from './services/auth.service';

@Component({
  selector: 'app-root',
  template: `
    <div class="app-container">
      <!-- Navigation Header -->
      <nav class="navbar" *ngIf="authService.isAuthenticated()">
        <div class="container">
          <div class="nav-brand">
            <h1>‚ù§Ô∏è JayMatch</h1>
            <span class="ku-badge">KU Verified</span>
          </div>
          <div class="nav-menu">
            <a routerLink="/matches" routerLinkActive="active">Matches</a>
            <a routerLink="/profile" routerLinkActive="active">Profile</a>
            <a routerLink="/chat" routerLinkActive="active">Chat</a>
            <button class="btn btn-outline" (click)="logout()">Logout</button>
          </div>
        </div>
      </nav>

      <!-- Main Content -->
      <main class="main-content">
        <router-outlet></router-outlet>
      </main>

      <!-- Footer -->
      <footer class="footer" *ngIf="authService.isAuthenticated()">
        <div class="container">
          <p>&copy; 2025 JayMatch - KU Dating App. Rock Chalk! üê¶</p>
        </div>
      </footer>
    </div>
  `,
  styles: [`
    .app-container {
      min-height: 100vh;
      display: flex;
      flex-direction: column;
    }

    .navbar {
      background: #1f1f1f;
      backdrop-filter: blur(10px);
      border-bottom: 1px solid #333333;
      padding: 1rem 0;
      position: sticky;
      top: 0;
      z-index: 1000;
    }

    .nav-brand {
      display: flex;
      align-items: center;
      gap: 12px;
    }

    .nav-brand h1 {
      color: #8b5cf6;
      font-size: 1.5rem;
      font-weight: 700;
    }

    .ku-badge {
      background: linear-gradient(135deg, #8b5cf6, #a855f7);
      color: white;
      padding: 4px 8px;
      border-radius: 6px;
      font-size: 0.75rem;
      font-weight: 600;
    }

    .nav-menu {
      display: flex;
      align-items: center;
      gap: 2rem;
    }

    .nav-menu a {
      text-decoration: none;
      color: #ffffff;
      font-weight: 500;
      transition: color 0.3s ease;
    }

    .nav-menu a:hover,
    .nav-menu a.active {
      color: #8b5cf6;
    }

    .main-content {
      flex: 1;
      padding: 2rem 0;
    }

    .footer {
      background: rgba(0, 0, 0, 0.1);
      padding: 1rem 0;
      text-align: center;
      color: rgba(255, 255, 255, 0.8);
    }

    @media (max-width: 768px) {
      .nav-menu {
        flex-direction: column;
        gap: 1rem;
      }
    }
  `]
})
export class AppComponent {
  constructor(public authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
