import { Component, OnInit } from '@angular/core';

interface Profile {
  id: number;
  name: string;
  age: number;
  major: string;
  year: string;
  bio: string;
  interests: string[];
  image: string;
}

@Component({
  selector: 'app-swipe-interface',
