export class SwipeInterfaceComponent implements OnInit {
  profiles: Profile[] = [];
  currentIndex = 0;
  currentProfile: Profile | null = null;
  likedProfiles: Profile[] = [];
  passedProfiles: Profile[] = [];
  
  // Touch/Mouse handling
  isDragging = false;
  startX = 0;
  startY = 0;
  currentX = 0;
  currentY = 0;
  cardTransform = '';
  cardOpacity = 1;
  swipeDirection = '';

