  onTouchStart(event: TouchEvent) {
    this.startX = event.touches[0].clientX;
    this.startY = event.touches[0].clientY;
    this.isDragging = true;
  }

  onTouchMove(event: TouchEvent) {
    if (!this.isDragging) return;
    
    this.currentX = event.touches[0].clientX;
    this.currentY = event.touches[0].clientY;
    this.updateCardTransform();
  }

  onTouchEnd(event: TouchEvent) {
    if (!this.isDragging) return;
    
    this.isDragging = false;
    this.handleSwipeEnd();
  }

  onMouseDown(event: MouseEvent) {
    this.startX = event.clientX;
    this.startY = event.clientY;
    this.isDragging = true;
    event.preventDefault();
  }

  onMouseMove(event: MouseEvent) {
    if (!this.isDragging) return;
    
    this.currentX = event.clientX;
    this.currentY = event.clientY;
    this.updateCardTransform();
  }

  onMouseUp(event: MouseEvent) {
    if (!this.isDragging) return;
    
    this.isDragging = false;
    this.handleSwipeEnd();
  }

  updateCardTransform() {
    const deltaX = this.currentX - this.startX;
    const deltaY = this.currentY - this.startY;
    const rotation = deltaX * 0.1;
    
    this.cardTransform = `translate(${deltaX}px, ${deltaY}px) rotate(${rotation}deg)`;
    
    // Update swipe direction
    if (Math.abs(deltaX) > 50) {
      this.swipeDirection = deltaX > 0 ? 'right' : 'left';
    } else {
      this.swipeDirection = '';
    }
    
    // Update opacity based on distance
    const distance = Math.abs(deltaX);
    this.cardOpacity = Math.max(0.3, 1 - distance / 300);
  }

  handleSwipeEnd() {
    const deltaX = this.currentX - this.startX;
    
    if (Math.abs(deltaX) > 100) {
      if (deltaX > 0) {
        this.swipeRight();
      } else {
        this.swipeLeft();
      }
    } else {
      // Reset card position
      this.cardTransform = '';
      this.cardOpacity = 1;
      this.swipeDirection = '';
    }
  }

  swipeRight() {
    if (!this.currentProfile) return;
    
    this.likedProfiles.push(this.currentProfile);
    this.nextProfile();
  }

  swipeLeft() {
    if (!this.currentProfile) return;
    
    this.passedProfiles.push(this.currentProfile);
    this.nextProfile();
  }

  nextProfile() {
    this.currentIndex++;
    this.currentProfile = this.profiles[this.currentIndex] || null;
    this.cardTransform = '';
    this.cardOpacity = 1;
    this.swipeDirection = '';
  }

  resetProfiles() {
    this.currentIndex = 0;
    this.currentProfile = this.profiles[0] || null;
    this.likedProfiles = [];
    this.passedProfiles = [];
  }
}
