import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ProfileService, Profile } from '../../../../services/profile.service';

@Component({
  selector: 'app-profile-edit',
  template: `
    <div class="profile-edit-container">
      <div class="profile-edit-header">
        <h1>Edit Profile</h1>
        <button class="btn btn-secondary" (click)="goBack()">
          Cancel
        </button>
      </div>

      <form [formGroup]="profileForm" (ngSubmit)="onSubmit()" class="profile-edit-form">
        <div class="form-section">
          <h2>Basic Information</h2>
          
          <div class="form-group">
            <label for="name">Full Name</label>
            <input 
              type="text" 
              id="name"
              formControlName="name"
              placeholder="Enter your full name"
            >
          </div>

          <div class="form-row">
            <div class="form-group">
              <label for="age">Age</label>
              <input 
                type="number" 
                id="age"
                formControlName="age"
                min="18"
                max="30"
              >
            </div>
            <div class="form-group">
              <label for="year">Year</label>
              <select id="year" formControlName="year">
                <option value="">Select year</option>
                <option value="Freshman">Freshman</option>
                <option value="Sophomore">Sophomore</option>
                <option value="Junior">Junior</option>
                <option value="Senior">Senior</option>
                <option value="Graduate">Graduate</option>
              </select>
            </div>
          </div>

          <div class="form-group">
            <label for="major">Major</label>
            <input 
              type="text" 
              id="major"
              formControlName="major"
              placeholder="e.g., Computer Science"
            >
          </div>

          <div class="form-group">
            <label for="bio">Bio</label>
            <textarea 
              id="bio"
              formControlName="bio"
              rows="4"
              placeholder="Tell us about yourself..."
            ></textarea>
          </div>
        </div>

        <div class="form-section">
          <h2>Interests</h2>
          <div class="interests-grid">
            <label 
              *ngFor="let interest of availableInterests" 
              class="interest-checkbox"
            >
              <input 
                type="checkbox" 
                [value]="interest"
                (change)="onInterestChange($event)"
                [checked]="selectedInterests.includes(interest)"
              >
              <span class="interest-label">{{ interest }}</span>
            </label>
          </div>
        </div>

        <div class="form-section">
          <h2>Preferences</h2>
          
          <div class="form-group">
            <label for="lookingFor">Looking For</label>
            <select id="lookingFor" formControlName="lookingFor">
              <option value="">Select what you're looking for</option>
              <option value="Serious Relationship">Serious Relationship</option>
              <option value="Casual Dating">Casual Dating</option>
              <option value="Friendship">Friendship</option>
              <option value="Study Buddy">Study Buddy</option>
            </select>
          </div>

          <div class="form-row">
            <div class="form-group">
              <label for="ageRangeMin">Min Age</label>
              <input 
                type="number" 
                id="ageRangeMin"
                formControlName="ageRangeMin"
                min="18"
                max="30"
              >
            </div>
            <div class="form-group">
              <label for="ageRangeMax">Max Age</label>
              <input 
                type="number" 
                id="ageRangeMax"
                formControlName="ageRangeMax"
                min="18"
                max="30"
              >
            </div>
          </div>

          <div class="form-group">
            <label for="maxDistance">Max Distance (miles)</label>
            <input 
              type="number" 
              id="maxDistance"
              formControlName="maxDistance"
              min="1"
              max="50"
            >
          </div>
        </div>

        <div class="form-actions">
          <button 
            type="submit" 
            class="btn btn-primary"
            [disabled]="profileForm.invalid || isLoading"
          >
            <span *ngIf="!isLoading">Save Changes</span>
            <span *ngIf="isLoading">Saving...</span>
          </button>
        </div>
      </form>

      <div class="error-message" *ngIf="errorMessage">
        {{ errorMessage }}
      </div>
    </div>
  `,
  styles: [`
    .profile-edit-container {
      max-width: 800px;
      margin: 0 auto;
      padding: 2rem;
    }

    .profile-edit-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 2rem;
    }

    .profile-edit-header h1 {
      color: white;
      font-size: 2.5rem;
    }

    .profile-edit-form {
      background: white;
      border-radius: 20px;
      padding: 2rem;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    }

    .form-section {
      margin-bottom: 2rem;
      padding-bottom: 2rem;
      border-bottom: 1px solid #e9ecef;
    }

    .form-section:last-child {
      border-bottom: none;
    }

    .form-section h2 {
      color: #667eea;
      margin-bottom: 1.5rem;
      font-size: 1.5rem;
    }

    .form-row {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 1rem;
    }

    .interests-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 1rem;
    }

    .interest-checkbox {
      display: flex;
      align-items: center;
      padding: 0.75rem;
      border: 2px solid #e9ecef;
      border-radius: 10px;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .interest-checkbox:hover {
      border-color: #667eea;
      background: #f8f9ff;
    }

    .interest-checkbox input[type="checkbox"] {
      margin-right: 0.75rem;
      transform: scale(1.2);
    }

    .interest-checkbox input[type="checkbox"]:checked + .interest-label {
      color: #667eea;
      font-weight: 600;
    }

    .interest-checkbox:has(input[type="checkbox"]:checked) {
      border-color: #667eea;
      background: #f8f9ff;
    }

    .interest-label {
      font-size: 0.9rem;
    }

    .form-actions {
      text-align: center;
      margin-top: 2rem;
    }

    .form-actions .btn {
      padding: 1rem 2rem;
      font-size: 1.1rem;
    }

    .error-message {
      background: #f8d7da;
      color: #721c24;
      padding: 1rem;
      border-radius: 8px;
      margin-top: 1rem;
      text-align: center;
    }

    @media (max-width: 768px) {
      .form-row {
        grid-template-columns: 1fr;
      }
      
      .interests-grid {
        grid-template-columns: 1fr;
      }
    }
  `]
})
export class ProfileEditComponent implements OnInit {
  profileForm: FormGroup;
  isLoading = false;
  errorMessage = '';
  selectedInterests: string[] = [];
  
  availableInterests = [
    'Sports', 'Music', 'Art', 'Photography', 'Travel', 'Cooking',
    'Gaming', 'Reading', 'Movies', 'Fitness', 'Dancing', 'Hiking',
    'Coffee', 'Wine', 'Dogs', 'Cats', 'Netflix', 'Concerts',
    'Museums', 'Beach', 'Mountains', 'City Life', 'Nature', 'Technology'
  ];

  constructor(
    private fb: FormBuilder,
    private profileService: ProfileService,
    private router: Router
  ) {
    this.profileForm = this.fb.group({
      name: ['', Validators.required],
      age: ['', [Validators.required, Validators.min(18), Validators.max(30)]],
      year: ['', Validators.required],
      major: ['', Validators.required],
      bio: ['', Validators.required],
      lookingFor: ['', Validators.required],
      ageRangeMin: [18, [Validators.required, Validators.min(18)]],
      ageRangeMax: [30, [Validators.required, Validators.max(30)]],
      maxDistance: [25, [Validators.required, Validators.min(1), Validators.max(50)]]
    });
  }

  ngOnInit() {
    this.loadProfile();
  }

  loadProfile() {
    this.profileService.getProfile().subscribe({
      next: (profile: any) => {
        this.profileForm.patchValue({
          name: profile.name,
          age: profile.age,
          year: profile.year,
          major: profile.major,
          bio: profile.bio,
          lookingFor: profile.lookingFor,
          ageRangeMin: profile.ageRange.min,
          ageRangeMax: profile.ageRange.max,
          maxDistance: profile.maxDistance
        });
        this.selectedInterests = [...profile.interests];
      },
      error: (error: any) => {
        this.errorMessage = 'Failed to load profile. Please try again.';
      }
    });
  }

  onInterestChange(event: any) {
    const interest = event.target.value;
    if (event.target.checked) {
      this.selectedInterests.push(interest);
    } else {
      this.selectedInterests = this.selectedInterests.filter(i => i !== interest);
    }
  }

  onSubmit() {
    if (this.profileForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';

      const formValue = this.profileForm.value;
      const profileData: Partial<Profile> = {
        ...formValue,
        interests: this.selectedInterests,
        ageRange: {
          min: formValue.ageRangeMin,
          max: formValue.ageRangeMax
        }
      };

      this.profileService.updateProfile(profileData).subscribe({
        next: () => {
          this.isLoading = false;
          this.router.navigate(['/profile']);
        },
        error: (error: any) => {
          this.isLoading = false;
          this.errorMessage = 'Failed to update profile. Please try again.';
        }
      });
    }
  }

  goBack() {
    this.router.navigate(['/profile']);
  }
}
