import { Component, OnInit } from '@angular/core';
import { ProfileService, Match } from '../../../../services/profile.service';
import { AuthService } from '../../../../services/auth.service';

@Component({
  selector: 'app-match-list',
  template: `
    <div class="matches-container">
      <div class="matches-header">
        <h1>Your Daily 10</h1>
        <p class="subtitle">Today's personalized matches just for you</p>
        <div class="match-stats">
          <span class="stat">
            <strong>{{ dailyMatches.length }}</strong> matches today
          </span>
          <span class="stat">
            <strong>{{ likedCount }}</strong> liked
          </span>
        </div>
      </div>

      <div class="matches-grid" *ngIf="dailyMatches.length > 0">
        <app-match-card 
          *ngFor="let match of dailyMatches; let i = index"
          [match]="match"
          [index]="i"
          (like)="onLike($event)"
          (pass)="onPass($event)"
        ></app-match-card>
      </div>

      <div class="no-matches" *ngIf="dailyMatches.length === 0 && !isLoading">
        <div class="no-matches-content">
          <h2>No matches today</h2>
          <p>Check back tomorrow for new matches!</p>
          <button class="btn btn-primary" (click)="refreshMatches()">
            Refresh Matches
          </button>
        </div>
      </div>

      <div class="loading" *ngIf="isLoading">
        <div class="loading-spinner"></div>
        <p>Finding your perfect matches...</p>
      </div>

      <div class="error-message" *ngIf="errorMessage">
        {{ errorMessage }}
      </div>
    </div>
  `,
  styles: [`
    .matches-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 2rem;
    }

    .matches-header {
      text-align: center;
      margin-bottom: 3rem;
    }

    .matches-header h1 {
      color: white;
      font-size: 2.5rem;
      margin-bottom: 0.5rem;
    }

    .subtitle {
      color: rgba(255, 255, 255, 0.8);
      font-size: 1.2rem;
      margin-bottom: 1rem;
    }

    .match-stats {
      display: flex;
      justify-content: center;
      gap: 2rem;
      margin-top: 1rem;
    }

    .stat {
      background: rgba(255, 255, 255, 0.1);
      padding: 0.5rem 1rem;
      border-radius: 20px;
      color: white;
      font-size: 0.9rem;
    }

    .matches-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 2rem;
      margin-bottom: 2rem;
    }

    .no-matches {
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 400px;
    }

    .no-matches-content {
      text-align: center;
      background: white;
      padding: 3rem;
      border-radius: 20px;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    }

    .no-matches-content h2 {
      color: #667eea;
      margin-bottom: 1rem;
    }

    .loading {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      min-height: 400px;
      color: white;
    }

    .loading-spinner {
      width: 50px;
      height: 50px;
      border: 4px solid rgba(255, 255, 255, 0.3);
      border-top: 4px solid white;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin-bottom: 1rem;
    }

    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }

    .error-message {
      background: #f8d7da;
      color: #721c24;
      padding: 1rem;
      border-radius: 8px;
      margin-top: 1rem;
      text-align: center;
    }

    @media (max-width: 768px) {
      .matches-grid {
        grid-template-columns: 1fr;
      }
      
      .match-stats {
        flex-direction: column;
        gap: 1rem;
      }
    }
  `]
})
export class MatchListComponent implements OnInit {
  dailyMatches: Match[] = [];
  isLoading = false;
  errorMessage = '';
  likedCount = 0;

  constructor(
    private profileService: ProfileService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.loadDailyMatches();
  }

  loadDailyMatches() {
    this.isLoading = true;
    this.errorMessage = '';

    this.profileService.getDailyMatches().subscribe({
      next: (matches: any) => {
        this.dailyMatches = matches;
        this.isLoading = false;
      },
      error: (error: any) => {
        this.errorMessage = 'Failed to load matches. Please try again.';
        this.isLoading = false;
      }
    });
  }

  onLike(match: Match) {
    this.profileService.likeUser(match.user.id).subscribe({
      next: () => {
        this.likedCount++;
        this.dailyMatches = this.dailyMatches.filter(m => m.id !== match.id);
      },
      error: (error: any) => {
        console.error('Error liking user:', error);
      }
    });
  }

  onPass(match: Match) {
    this.profileService.passUser(match.user.id).subscribe({
      next: () => {
        this.dailyMatches = this.dailyMatches.filter(m => m.id !== match.id);
      },
      error: (error: any) => {
        console.error('Error passing user:', error);
      }
    });
  }

  refreshMatches() {
    this.loadDailyMatches();
  }
}
