import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../../../services/auth.service';

@Component({
  selector: 'app-register',
  template: `
    <div class="auth-container">
      <div class="auth-card">
        <div class="auth-header">
          <h1>❤️ JayMatch</h1>
          <p class="subtitle">Join the KU dating community!</p>
        </div>

        <form [formGroup]="registerForm" (ngSubmit)="onSubmit()" class="auth-form">
          <div class="form-group">
            <label for="email">KU Email</label>
            <input 
              type="email" 
              id="email"
              formControlName="email"
              placeholder="yourname@ku.edu"
              [class.error]="registerForm.get('email')?.invalid && registerForm.get('email')?.touched"
            >
            <div class="error-message" *ngIf="registerForm.get('email')?.invalid && registerForm.get('email')?.touched">
              <span *ngIf="registerForm.get('email')?.errors?.['required']">Email is required</span>
              <span *ngIf="registerForm.get('email')?.errors?.['email']">Please enter a valid email</span>
              <span *ngIf="registerForm.get('email')?.errors?.['kuEmail']">Must be a KU email (&#64;ku.edu)</span>
            </div>
          </div>

          <div class="form-group">
            <label for="password">Password</label>
            <input 
              type="password" 
              id="password"
              formControlName="password"
              placeholder="Create a strong password"
              [class.error]="registerForm.get('password')?.invalid && registerForm.get('password')?.touched"
            >
            <div class="error-message" *ngIf="registerForm.get('password')?.invalid && registerForm.get('password')?.touched">
              <span *ngIf="registerForm.get('password')?.errors?.['required']">Password is required</span>
              <span *ngIf="registerForm.get('password')?.errors?.['minlength']">Password must be at least 8 characters</span>
            </div>
          </div>

          <div class="form-group">
            <label for="name">Full Name</label>
            <input 
              type="text" 
              id="name"
              formControlName="name"
              placeholder="Enter your full name"
              [class.error]="registerForm.get('name')?.invalid && registerForm.get('name')?.touched"
            >
            <div class="error-message" *ngIf="registerForm.get('name')?.invalid && registerForm.get('name')?.touched">
              Name is required
            </div>
          </div>

          <div class="form-row">
            <div class="form-group">
              <label for="age">Age</label>
              <input 
                type="number" 
                id="age"
                formControlName="age"
                placeholder="18"
                min="18"
                max="30"
                [class.error]="registerForm.get('age')?.invalid && registerForm.get('age')?.touched"
              >
              <div class="error-message" *ngIf="registerForm.get('age')?.invalid && registerForm.get('age')?.touched">
                Age is required (18-30)
              </div>
            </div>

            <div class="form-group">
              <label for="year">Year</label>
              <select 
                id="year"
                formControlName="year"
                [class.error]="registerForm.get('year')?.invalid && registerForm.get('year')?.touched"
              >
                <option value="">Select year</option>
                <option value="Freshman">Freshman</option>
                <option value="Sophomore">Sophomore</option>
                <option value="Junior">Junior</option>
                <option value="Senior">Senior</option>
                <option value="Graduate">Graduate</option>
              </select>
              <div class="error-message" *ngIf="registerForm.get('year')?.invalid && registerForm.get('year')?.touched">
                Year is required
              </div>
            </div>
          </div>

          <div class="form-group">
            <label for="major">Major</label>
            <input 
              type="text" 
              id="major"
              formControlName="major"
              placeholder="e.g., Computer Science"
              [class.error]="registerForm.get('major')?.invalid && registerForm.get('major')?.touched"
            >
            <div class="error-message" *ngIf="registerForm.get('major')?.invalid && registerForm.get('major')?.touched">
              Major is required
            </div>
          </div>

          <button 
            type="submit" 
            class="btn btn-primary full-width"
            [disabled]="registerForm.invalid || isLoading"
          >
            <span *ngIf="!isLoading">Create Account</span>
            <span *ngIf="isLoading">Creating Account...</span>
          </button>
        </form>

        <!-- Demo Bypass Button -->
        <div class="demo-bypass">
          <button 
            class="btn btn-demo"
            (click)="demoBypass()"
            [disabled]="isLoading"
          >
            🚀 Demo Bypass (Skip Registration)
          </button>
          <p class="demo-note">For demo purposes only</p>
        </div>

        <div class="auth-footer">
          <p>Already have an account? <a routerLink="/login">Sign in</a></p>
        </div>

        <div class="error-alert" *ngIf="errorMessage">
          {{ errorMessage }}
        </div>
      </div>
    </div>
  `,
  styles: [`
    .auth-container {
      min-height: 100vh;
      background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
      display: flex;
      align-items: center;
      justify-content: center;
      padding: 2rem;
    }

    .auth-card {
      background: #1f1f1f;
      border-radius: 20px;
      padding: 3rem;
      box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
      width: 100%;
      max-width: 500px;
      border: 1px solid #333333;
    }

    .auth-header {
      text-align: center;
      margin-bottom: 2rem;
    }

    .auth-header h1 {
      background: linear-gradient(135deg, #8b5cf6, #a855f7);
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      font-size: 2.5rem;
      margin-bottom: 0.5rem;
    }

    .subtitle {
      color: #a0a0a0;
      font-size: 1.1rem;
    }

    .auth-form {
      margin-bottom: 2rem;
    }

    .form-group {
      margin-bottom: 1.5rem;
    }

    .form-group label {
      display: block;
      margin-bottom: 0.5rem;
      color: #ffffff;
      font-weight: 500;
    }

    .form-row {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 1rem;
    }

    input {
      width: 100%;
      padding: 1rem;
      border: 2px solid #333333;
      border-radius: 12px;
      background: #1f1f1f;
      color: #ffffff;
      font-size: 1rem;
      transition: all 0.3s ease;
    }

    input:focus {
      outline: none;
      border-color: #8b5cf6;
      box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.1);
    }

    input.error {
      border-color: #ff6b6b;
    }

    select {
      width: 100%;
      padding: 1rem;
      border: 2px solid #333333;
      border-radius: 12px;
      background: #1f1f1f;
      color: #ffffff;
      font-size: 1rem;
      transition: all 0.3s ease;
    }

    select:focus {
      outline: none;
      border-color: #8b5cf6;
      box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.1);
    }

    select.error {
      border-color: #ff6b6b;
    }

    .btn {
      width: 100%;
      padding: 1rem;
      border: none;
      border-radius: 12px;
      font-size: 1rem;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
      margin-bottom: 1rem;
    }

    .btn-primary {
      background: linear-gradient(135deg, #8b5cf6, #a855f7);
      color: white;
    }

    .btn-primary:hover {
      transform: translateY(-2px);
      box-shadow: 0 8px 25px rgba(139, 92, 246, 0.3);
    }

    .btn-primary:disabled {
      opacity: 0.6;
      cursor: not-allowed;
      transform: none;
    }

    .auth-footer {
      text-align: center;
      color: #a0a0a0;
    }

    .auth-footer a {
      color: #8b5cf6;
      text-decoration: none;
      font-weight: 500;
    }

    .auth-footer a:hover {
      text-decoration: underline;
    }

    .error-message {
      color: #ff6b6b;
      font-size: 0.875rem;
      margin-top: 0.25rem;
    }

    .error-alert {
      background: rgba(255, 107, 107, 0.1);
      color: #ff6b6b;
      padding: 1rem;
      border-radius: 8px;
      margin-top: 1rem;
      border: 1px solid rgba(255, 107, 107, 0.2);
    }

    .demo-bypass {
      text-align: center;
      margin: 1.5rem 0;
      padding: 1rem;
      background: rgba(255, 255, 255, 0.05);
      border-radius: 12px;
      border: 2px dashed rgba(255, 255, 255, 0.2);
    }

    .btn-demo {
      background: linear-gradient(135deg, #28a745, #20c997);
      color: white;
      border: none;
      padding: 0.75rem 1.5rem;
      border-radius: 12px;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
      font-size: 0.9rem;
    }

    .btn-demo:hover:not(:disabled) {
      transform: translateY(-2px);
      box-shadow: 0 8px 25px rgba(40, 167, 69, 0.3);
    }

    .btn-demo:disabled {
      opacity: 0.6;
      cursor: not-allowed;
    }

    .demo-note {
      font-size: 0.75rem;
      color: rgba(255, 255, 255, 0.7);
      margin-top: 0.5rem;
      font-style: italic;
    }

    @media (max-width: 480px) {
      .form-row {
        grid-template-columns: 1fr;
      }
      
      .auth-card {
        padding: 2rem;
        margin: 1rem;
      }
    }
  `]
})
export class RegisterComponent {
  registerForm: FormGroup;
  isLoading = false;
  errorMessage = '';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      email: ['', [Validators.required, Validators.email, this.kuEmailValidator]],
      password: ['', [Validators.required, Validators.minLength(8)]],
      name: ['', Validators.required],
      age: ['', [Validators.required, Validators.min(18), Validators.max(30)]],
      year: ['', Validators.required],
      major: ['', Validators.required]
    });
  }

  kuEmailValidator(control: any) {
    const email = control.value;
    if (email && !email.endsWith('@ku.edu')) {
      return { kuEmail: true };
    }
    return null;
  }

  onSubmit() {
    if (this.registerForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';

      this.authService.register(this.registerForm.value).subscribe({
        next: (response: any) => {
          this.isLoading = false;
          this.router.navigate(['/profile']);
        },
        error: (error: any) => {
          this.isLoading = false;
          this.errorMessage = error.error?.message || 'Registration failed. Please try again.';
        }
      });
    }
  }

  demoBypass() {
    // Demo bypass - simulate successful registration
    this.isLoading = true;
    
    // Simulate API call delay
    setTimeout(() => {
      // Set demo token and user data
      localStorage.setItem('jaymatch_token', 'demo-token-12345');
      localStorage.setItem('jaymatch_user', JSON.stringify({
        id: 1,
        email: 'demo@ku.edu',
        name: 'Demo User',
        age: 22,
        major: 'Computer Science',
        year: 'Senior',
        bio: 'Demo user for testing',
        interests: ['Programming', 'Coffee', 'Movies'],
        isVerified: true
      }));
      
      // Trigger authentication state change
      this.authService['currentUserSubject'].next({
        id: 1,
        email: 'demo@ku.edu',
        name: 'Demo User',
        age: 22,
        major: 'Computer Science',
        year: 'Senior',
        bio: 'Demo user for testing',
        interests: ['Programming', 'Coffee', 'Movies'],
        isVerified: true
      });
      
      this.isLoading = false;
      this.router.navigate(['/matches']);
    }, 1000);
  }
}
